var maxTemp = 60.0;
var minTemp = -10.0;
var maxHumid = 100.0;
var minHumid = 0.0;

var initTemp = 41.0;
var initHumid = 85.0;

var randTemp = 100;
var randHumid = 100;

var temp = global.get("temp") || initTemp;
var humidity = global.get("humidity") || initHumid;

var tempAdj = Math.floor((Math.random() * randTemp) -  (randTemp/2))/ randTemp;
var humidAdj = Math.floor((Math.random() * randHumid) - (randHumid/2))/randHumid;

temp += tempAdj;
humidity += humidAdj;

if(temp > maxTemp)
    temp = maxTemp;
else if(temp < minTemp)
    temp = minTemp;

if(humidity > maxHumid)
    humidity = maxHumid;
else if(humidity < minHumid)
    humidity = minHumid;

temp = Math.round(temp*100)/100;
humidity = Math.round(humidity*100)/100;

global.set("temp",temp);
global.set("humidity",humidity);

